import React from "react"
import { Slot } from "@radix-ui/react-slot"

import { useVolumeStates } from "@/registry/default/hooks/useVolumeStates"
import { useMediaStore } from "@/registry/default/ui/MediaProvider"

export interface MuteControlProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  asChild?: boolean
}

// eslint-disable-next-line react/display-name
export const MuteControl = React.forwardRef<
  HTMLButtonElement,
  MuteControlProps
>((props, forwardedRef) => {
  useVolumeStates()

  const Comp = props.asChild ? Slot : "button"
  const toggleMute = useMediaStore((state) => state.toggleMute)
  const muted = useMediaStore((state) => state.muted)

  return (
    <Comp
      data-slot="mute-control"
      {...props}
      ref={forwardedRef}
      aria-label="Mute (keyboard shortcut m)"
      aria-keyshortcuts="m"
      onClick={toggleMute}
    />
  )
})
