"use client"

import { Button } from "@/components/ui/button"
import { PauseIcon } from "@/components/icons/PauseIcon"
import { PlayIcon } from "@/components/icons/PlayIcon"
import { ReplayIcon } from "@/components/icons/ReplayIcon"
import { Spinner } from "@/components/icons/Spinner"
import { useMediaStore } from "@/registry/default/ui/MediaProvider"
import { PlaybackControl } from "@/registry/default/ui/PlaybackControl"

import { PlayerDemoLayout } from "./player-demo-root"

function PlaybackControlExample() {
  const status = useMediaStore((state) => state.status)

  return (
    <Button size="icon" variant="glass" asChild>
      <PlaybackControl>
        {status === "playing" ? (
          <PauseIcon />
        ) : status === "ended" ? (
          <ReplayIcon />
        ) : status === "buffering" ? (
          <Spinner />
        ) : (
          <PlayIcon />
        )}
      </PlaybackControl>
    </Button>
  )
}

export function PlaybackStateControl() {
  return (
    <PlayerDemoLayout>
      <PlaybackControlExample />
    </PlayerDemoLayout>
  )
}
